// import useCharacter from "../../hooks/useCharacter";
import { useDispatch } from "react";
import { useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { useEffect } from "react";
import { getCharacterDetail, cleanDetail } from "../../redux/actions";

const Detail = () => {
  const character = function useCharacter() {
    const dispatch = useDispatch();
    const characters = useSelector((state) => state.characterDetail);
    const { detailId } = useParams();

    useEffect(() => {
      dispatch(getCharacterDetail(detailId));
      return () => {
        dispatch(cleanDetail());
      };
    }, [detailId]);

    return characters;
  };

  return (
    <div>
      {/* Acá le vamos a decir lo siguiente. character.name existe? si existe, mostrame esto (y ahí la info) Si no existe, mostrame loading */}
      {character.name ? (
        <>
          <h2>{character.name}</h2>
          <p>{character.status}</p>
          <p>{character.species}</p>
          <p>{character.gender}</p>
          {/* Acá le decimos que espere hasta que la info de origin esté disponible, porque a veces axios tarda mucho */}
          <p>{character.origin?.name}</p>
          <img src={character.image} alt="img" />
        </>
      ) : (
        <h3>Loading...</h3>
      )}
    </div>
  );
};

export default Detail;
